mapper.update:
UPDATE INQUIRY
SET CNT = NVL(CNT, 0) + 1
WHERE INQUIRY_NO = #{inquiryNo}

mapper.select:
SELECT PASSWORD
FROM INQUIRY
WHERE INQUIRY_NO = #{inquiryNo}

SELECT PASSWORD
FROM PRODUCT_QNA
WHERE QNA_NO = #{qnaNo}

mapper.update:
UPDATE PRODUCT_QNA
SET CNT = NVL(CNT, 0) + 1
WHERE QNA_NO = #{qnaNo}
