mapper.update:
UPDATE ORDERS
SET
    STATUS = #{status},
    UDATETIME = SYSDATE
WHERE ORDER_NO = #{orderNo}
AND EXISTS (
    SELECT 1
    FROM ORDER_ITEM oi
    JOIN PRODUCT p ON oi.PRODUCT_NO = p.PRODUCT_NO
    WHERE oi.ORDER_NO = #{orderNo}
    AND p.SELLER_ID = #{sellerId}
)

mapper.select:
SELECT
    o.ORDER_NO,
    o.STATUS
FROM ORDERS o
WHERE o.ORDER_NO = #{orderNo}
AND EXISTS (
    SELECT 1
    FROM ORDER_ITEM oi
    JOIN PRODUCT p ON oi.PRODUCT_NO = p.PRODUCT_NO
    WHERE oi.ORDER_NO = #{orderNo}
    AND p.SELLER_ID = #{sellerId}
)
