mapper.update:
UPDATE SELLER_INFO
SET
    BUSINESS_NAME = #{businessName} 
WHERE USER_ID = #{userId}

UPDATE SELLER_INFO
SET
    ACCOUNT = #{account, jdbcType=VARCHAR},
    BANKNAME = #{bankName, jdbcType=VARCHAR}
WHERE USER_ID = #{userId}

mapper.select:
SELECT COUNT(PRODUCT_NO)
FROM PRODUCT
WHERE SELLER_ID = #{sellerId}
